mapper.select:
SELECT type_code 
  FROM acct_type 
 WHERE type_name = #{typeName}

SELECT class_code 
  FROM acct_class 
 WHERE class_name = #{className}

SELECT subclass_code 
  FROM acct_subclass 
 WHERE subclass_name = #{subclassName}

mapper.insert:
	INSERT into account(
		   acct_code
		 , name
		 , acct_type
		 , acct_class
	     , acct_subclass
		 , acct_yn
    	 , note
		 , created_date
		 , update_date
		 , com_id)
    VALUES (
    	   #{acctCode}
		 , #{name}
		 , #{acctType}
		 , #{acctClass}
   		 , #{acctSubclass}
		 , #{acctYn}
         , #{note}
		 , SYSDATE
		 , SYSDATE
		 , 'COM-101'
		)
