mapper.select:
SELECT c.created_date AS createdDate, c.chit_title AS chitTitle, '' AS parent, c.chit_code AS chitCode, getVdrName((SELECT vdr_code FROM orders WHERE ord_code = article_code)) AS vdrCode, article_code, c.supply_price AS supplyPrice, c.vat_price AS vatPrice, c.tot_price AS totPrice, 0 AS iPrice, c.tax_code AS taxCode, getSubCode(c.status) AS status, getSubCode(c.type) AS tType, getDeptName(c.dept_code) AS deptCode, getEmpName(c.phy_emp_id, #{comId}) AS phyEmpId, getEmpName(c.appr_emp_id, #{comId}) AS apprEmpId, c.note AS note, '' AS acctCode, '' AS iType, '' AS iName, '' AS aName, CASE getSubCode(c.status) WHEN '등록' THEN 1 WHEN '결제' THEN 2 WHEN '반려' THEN 3 WHEN '결제완료' THEN 4 ELSE 9 END AS sort_order FROM chit c WHERE com_id = #{comId}
UNION ALL
SELECT i.created_date AS createdDate, '' AS chitTitle, i.chit_code AS parent, i.chit_dt_id AS chitCode, '' AS vdrCode, '' AS article_code, 0 AS supplyPrice, 0 AS vatPrice, 0 AS totPrice, i.price AS iPrice, '' AS taxCode, '' AS status, '' AS tType, '' AS deptCode, '' AS phyEmpId, '' AS apprEmpId, i.note AS note, i.acct_code AS acctCode, i.type AS iType, getSubCode(i.type) AS iName, a.name AS aName, 99 AS sort_order -- 자식은 가장 마지막에 오게 정렬 (혹은 부모 따라 정렬하게 할 수도 있음) FROM indignation i LEFT JOIN account a ON i.acct_code = a.acct_code WHERE i.com_id = #{comId} ORDER BY sort_order ASC, createdDate DESC

SELECT status 
  FROM chit 
 WHERE chit_code = #{chitCode}
   AND com_id= #{comId}

mapper.insert:

    SELECT
      #{chitCode} || '-' || LPAD(NVL(MAX(TO_NUMBER(SUBSTR(chit_dt_id, INSTR(chit_dt_id, '-', -1) + 1))), 0) + 1, 2, '0') AS chitDtId
    FROM indignation
    WHERE chit_code = #{chitCode}
      AND com_id = #{comId}

   INSERT INTO indignation (
		  chit_dt_id
		, chit_code
		, acct_code
		, type
		, price
		, created_date
		, com_id)
   VALUES (
		  #{chitDtId}
		, #{chitCode}
		, #{acctCode}
		, #{iType}
		, #{iPrice}
		, SYSDATE
		, #{comId}
		)

mapper.update:
UPDATE chit
SET 
  phy_emp_id = #{empId}
, status = 'cst02'
, note = #{note}
, update_date = SYSDATE
WHERE chit_code = #{chitCode}
  AND com_id= #{comId}

mapper.delete:
   DELETE FROM indignation
	WHERE chit_dt_id = #{chitDtId}
	  AND com_id= #{comId}

mapper.update:
UPDATE chit
   SET status = 'cst04'
     , update_date = SYSDATE
 WHERE chit_code = #{chitCode}
   AND com_id= #{comId}

UPDATE vender
   SET credit_bal_price = credit_bal_price + #{totPrice}
 WHERE com_id= #{comId}
   AND vdr_code = (
       SELECT vdr_code
         FROM orders
        WHERE ord_code = #{articleCode}
 		)

mapper.select:
SELECT type 
  FROM chit 
 WHERE chit_code = #{chitCode}
   AND com_id= #{comId}
