mapper.insert:

	SELECT 'txh-' || LPAD(NVL(MAX(TO_NUMBER(SUBSTR(txn_hist_code, 5))), 0) + 1, 3, '0') AS txn_code
	FROM transaction

INSERT INTO transaction (
txn_hist_code
, txn_date
, txn_type
, txn_name
, txn_memo
, price
, bank
, acct_no
, com_id
) VALUES (
#{txnHistCode}
, SYSDATE
, #{txnType}
, #{txnName}
, #{txnMemo}
, #{price}
, #{bank}
, #{acctNo}
, #{comId}
)
