mapper.select:
SELECT e.emp_id, e.com_id, e.dept_code, getDeptName(e.dept_code) AS dept_name, e.pwd, getEmpName(e.emp_id,#{comId}) AS emp_name, e.email, e.tel, e.addr, e.addr_detail AS addrDetail, e.hire_date, e.position, (SELECT s.SUB_NAME FROM SUBCODES s WHERE s.MAIN_CODE = 'pos' AND s.SUB_CODE = e.position) AS positionName, e.resign_date, e.emp_status, (SELECT s.SUB_NAME FROM SUBCODES s WHERE s.MAIN_CODE = 'stt' AND s.SUB_CODE = e.emp_status) AS empStatusName, e.work_type, (SELECT s.SUB_NAME FROM SUBCODES s WHERE s.MAIN_CODE = 'emp' AND s.SUB_CODE = e.work_type) AS workTypeName, e.resign_reason, e.sal, e.bank, e.acct_no, e.birth_date, e.profile_img_path, e.note, e.annual_leave_total AS annualLeaveTotal, e.annual_leave_used AS annualLeaveUsed, (e.annual_leave_total - e.annual_leave_used) AS annualLeaveRemain, -- 근로계약 정보 (최신 계약) c.cont_type AS contractType, c.start_date AS contractStartDate, c.end_date AS contractEndDate, c.prob_period AS probationPeriod, c.ann_sal AS annualSalary, c.mon_sal AS monthlySalary, c.bonus, c.extra_allow AS additionalAllowance, -- 근태관리 정보 (기본 근무패턴) c.work_start AS workStartTime, c.work_end AS workEndTime, c.DAY_WORK_HRS AS workPattern -- 급여정보 (최신 급여명세서) -- ss.meal_allow AS mealAllowance FROM employees e LEFT JOIN ( SELECT emp_id, com_id, cont_type, start_date, end_date, prob_period, ann_sal, mon_sal, bonus, extra_allow, work_start, work_end, day_work_hrs FROM contract WHERE com_id = #{comId} AND (emp_id, created_date) IN ( SELECT emp_id, MAX(created_date) FROM contract WHERE com_id = #{comId} GROUP BY emp_id ) ) c ON e.emp_id = c.emp_id AND e.com_id = c.com_id LEFT JOIN ( SELECT ss.emp_id, ss.COM_ID FROM ( SELECT ss.emp_id, ss.COM_ID, ss.CALC_MONTH, ROW_NUMBER() OVER (PARTITION BY ss.emp_id ORDER BY ss.CALC_MONTH DESC) as rn FROM salarystatement ss WHERE ss.COM_ID = #{comId} ) ss WHERE rn = 1 ) ss ON e.emp_id = ss.emp_id AND e.com_id = ss.COM_ID
e.com_id = #{comId}
AND (e.emp_id LIKE '%' || #{keyword} || '%' OR getEmpName(e.emp_id,#{comId}) LIKE '%' || #{keyword} || '%' OR getDeptName(e.dept_code) LIKE '%' || #{keyword} || '%')
AND e.emp_status = #{status}
AND e.dept_code = #{dept}
ORDER BY e.emp_id

mapper.update:
UPDATE employees SET com_id = #{comId}, dept_code = #{deptCode},
pwd = #{pwd},
emp_name = #{empName}, email = #{email}, tel = #{tel}, addr = #{addr}, addr_detail = #{addrDetail}, hire_date = #{hireDate}, position = #{position}, resign_date = #{resignDate}, emp_status = #{empStatus}, work_type = #{workType}, resign_reason = #{resignReason}, sal = #{sal}, bank = #{bank}, acct_no = #{acctNo}, birth_date = #{birthDate}, profile_img_path = #{profileImgPath}, note = #{note}, annual_leave_total = #{annualLeaveTotal}, annual_leave_used = #{annualLeaveUsed} WHERE emp_id = #{empId} AND com_id = #{comId}

UPDATE employees
SET annual_leave_total = #{totalLeave},
	annual_leave_used = #{usedLeave}
WHERE emp_id = #{empId}
AND com_id = #{comId}

UPDATE employees
SET annual_leave_used = annual_leave_used + #{usedDays}
WHERE emp_id = #{empId}
AND com_id = #{comId}
AND (annual_leave_total - annual_leave_used) >= #{usedDays}

mapper.select:
SELECT ALLOW_CODE, AMOUNT, NOTE
FROM EMPALLOWANCE
WHERE EMP_ID = #{empId}
  AND COM_ID = #{comId}
ORDER BY ALLOW_CODE

mapper.update:
UPDATE EMPALLOWANCE 
SET AMOUNT = #{amount}, NOTE = #{note}, UPDATED_DATE = SYSDATE
WHERE EMP_ID = #{empId} 
  AND COM_ID = #{comId} 
  AND ALLOW_CODE = #{allowCode}

mapper.delete:
DELETE FROM EMPALLOWANCE
WHERE EMP_ID = #{empId} 
  AND COM_ID = #{comId} 
  AND ALLOW_CODE = #{allowCode}

DELETE FROM EMPALLOWANCE
WHERE EMP_ID = #{empId} 
  AND COM_ID = #{comId}
