mapper.select:
SELECT ALLOW_CODE, ALLOW_NAME, TYPE, DEFAULT_AMOUNT, ACCT_YN, TO_CHAR(CREATED_DATE, 'YYYY-MM-DD') as CREATED_DATE, NVL(NOTE, '') as NOTE, COM_ID, CALC_TYPE, WORK_TYPES, TAX_FREE_YN, (SELECT LISTAGG(SUB_NAME, ',') WITHIN GROUP (ORDER BY SUB_CODE) FROM SUBCODES WHERE MAIN_CODE = 'emp' AND INSTR(',' || WORK_TYPES || ',', ',' || SUB_CODE || ',') > 0 ) AS WORK_TYPE_NAMES FROM SALARYITEM WHERE COM_ID = #{comId}
AND TYPE = #{type}
AND ACCT_YN = #{acctYn}
AND (ALLOW_NAME LIKE '%' || #{keyword} || '%' OR ALLOW_CODE LIKE '%' || #{keyword} || '%')
ORDER BY SORT_ORDER, ALLOW_CODE

SELECT * FROM SALARYITEM
WHERE COM_ID = #{comId}
  AND ALLOW_CODE = #{allowCode}

mapper.insert:

    SELECT NVL(MAX(ID), 1000) + 1 FROM SALARYITEM

INSERT INTO SALARYITEM (
    ALLOW_CODE, ALLOW_NAME, TYPE, DEFAULT_AMOUNT, ACCT_YN, CREATED_DATE, NOTE, COM_ID, CALC_TYPE, WORK_TYPES
) VALUES (
    #{allowCode}, #{allowName}, #{type}, #{defaultAmount}, #{acctYn}, SYSDATE, #{note}, #{comId}, #{calcType}, #{workTypes}
)

mapper.update:
UPDATE SALARYITEM SET
    ALLOW_NAME = #{allowName},
    TYPE = #{type},
    DEFAULT_AMOUNT = #{defaultAmount},
    ACCT_YN = #{acctYn},
    NOTE = #{note},
    CALC_TYPE = #{calcType},
    WORK_TYPES = #{workTypes}
WHERE COM_ID = #{comId}
  AND ALLOW_CODE = #{allowCode}

mapper.delete:
DELETE FROM SALARYITEM
WHERE COM_ID = #{comId}
  AND ALLOW_CODE = #{allowCode}
