mapper.select:
SELECT allow_code, allow_name, type, default_amount, acct_yn, TO_CHAR(created_date, 'YYYY-MM-DD') as created_date, NVL(note, '') as note, com_id, calc_type, work_types, -- 적용대상 한글명 콤마로 (SELECT LISTAGG(sub_name, ',') WITHIN GROUP (ORDER BY sub_code) FROM subcodes WHERE main_code = 'emp' AND INSTR(',' || work_types || ',', ',' || sub_code || ',') > 0 ) AS work_type_names FROM salaryitem WHERE com_id = #{comId}
AND type = #{type}
AND acct_yn = #{acctYn}
AND (allow_name LIKE '%' || #{keyword} || '%' OR allow_code LIKE '%' || #{keyword} || '%')
ORDER BY sort_order, allow_code

SELECT * FROM salaryitem
WHERE com_id = #{comId}
  AND allow_code = #{allowCode}

mapper.insert:

    SELECT NVL(MAX(id), 1000) + 1 FROM salaryitem

INSERT INTO salaryitem (
    allow_code, allow_name, type, default_amount, acct_yn, created_date, note, com_id, calc_type, work_types
) VALUES (
    #{allowCode}, #{allowName}, #{type}, #{defaultAmount}, #{acctYn}, SYSDATE, #{note}, #{comId}, #{calcType}, #{workTypes}
)

mapper.update:
UPDATE salaryitem
SET allow_name = #{allowName},
    type = #{type},
    default_amount = #{defaultAmount},
    acct_yn = #{acctYn},
    note = #{note},
    calc_type = #{calcType},
    work_types = #{workTypes}
WHERE allow_code = #{allowCode}
  AND com_id = #{comId}

mapper.delete:
DELETE FROM salaryitem
WHERE com_id = #{comId}
  AND allow_code = #{allowCode}
