mapper.select:
SELECT SS.SAL_ID as salaryId, SS.EMP_ID as empId, E.EMP_NAME as empName, D.DEPT_NAME as deptName, E.POSITION as position, -- 직위명은 코드/조인으로 변환 필요 SS.CALC_MONTH as calcMonth, SS.BASE_SAL as baseSalary, SS.NET_SALARY as netSalary, SS.STATUS as status, SS.CREATED_DATE as createdDate, SS.APPROVE_DATE as approveDate, SS.APPROVE_USER as approveUser, SS.PAY_DATE as payDate, SS.PAY_USER as payUser FROM SALARYSTATEMENT SS LEFT JOIN EMPLOYEES E ON SS.EMP_ID = E.EMP_ID LEFT JOIN DEPARTMENTS D ON E.DEPT_CODE = D.DEPT_CODE
AND SS.CALC_MONTH = #{calcMonth}
AND E.EMP_NAME LIKE CONCAT('%', #{empName}, '%')
AND SS.STATUS = #{status}
ORDER BY SS.CALC_MONTH DESC, E.EMP_NAME ASC

mapper.update:
UPDATE SALARYSTATEMENT
SET 
    STATUS = #{status}

        , APPROVE_DATE = #{approveDate}
        , APPROVE_USER = #{approveUser}


        , PAY_DATE = #{payDate}
        , PAY_USER = #{payUser}

    , UPDATED_DATE = SYSDATE
    , UPDATE_USER = #{approveUser}
WHERE SAL_ID = #{salaryId}
