mapper.select:
SELECT s.sal_id AS sal_id, s.emp_id AS emp_id, s.com_id AS com_id, s.calc_month AS calc_month, s.base_sal AS base_sal, s.bonus AS bonus, s.ot_allow AS ot_allow, s.holi_allow AS holi_allow, s.meal_allow AS meal_allow, s.inc_tax AS inc_tax, s.nat_pension AS nat_pension, s.health_insur AS health_insur, s.emp_insur AS emp_insur, s.ltc_insur AS ltc_insur, s.net_salary AS net_salary, s.status AS status, e.emp_name AS emp_name, e.dept_code AS dept_code, d.dept_name AS dept_name FROM salarystatement s JOIN employees e ON s.emp_id = e.emp_id AND s.com_id = e.com_id LEFT JOIN departments d ON e.dept_code = d.dept_code WHERE s.com_id = #{comId}
AND s.emp_id = #{emp_id}
AND s.calc_month = #{calc_month}
AND (e.emp_name LIKE '%' || #{keyword} || '%' OR s.sal_id LIKE '%' || #{keyword} || '%')
ORDER BY s.calc_month DESC

SELECT ALLOW_CODE, AMOUNT, NOTE
FROM EMP_ALLOWANCE
WHERE EMP_ID = #{empId}
  AND COM_ID = #{comId}
ORDER BY ALLOW_CODE
