mapper.update:
update vender
set credit_bal_price=#{creditBalPrice}-#{totPrice}
where vdr_code=#{vdrCode}
AND com_id=#{comId}

{ CALL
update_credit_bal(
#{vdrId, mode=IN, jdbcType=VARCHAR},
#{totPrice,
mode=IN, jdbcType=NUMERIC}
) }

mapper.delete:
delete from orders
where ord_code=#{ordCode}
and   com_id=#{comId}

delete from ordersdetail
where ord_code=#{ordCode}
AND com_id=#{comId}

mapper.update:
UPDATE ORDERSDETAIL
SET item_code = #{itemCode},
quantity=#{quantity},
sup_price=#{supPrice},
vat_price=#{vatPrice},
tot_price=#{totPrice},
disc_price=#{discPrice},
com_id=#{comId} 
WHERE ord_dtl_code = #{ordDtlCode}
AND com_id=#{comId}

mapper.delete:
DELETE FROM ORDERSDETAIL
WHERE ord_dtl_code = #{ordDtlCode}
AND com_id=#{comId}

mapper.update:
UPDATE vender
SET credit_bal_price = credit_bal_price
- #{totPrice}
WHERE vdr_code = #{vdrCode}
AND com_id=#{comId}

mapper.select:
	select Max(ord_code)
    from orders
    where com_id=#{comId}
