mapper.select:
SELECT vdr_code, type, vdr_name, biz_no, ceo_name, addr, addr_detail, email, tel, bank, acct_no, credit_period, credit_price, credit_bal_price, discount, com_id FROM vender WHERE type='vt02' AND com_id=#{comId}
AND vdr_code LIKE '%'||#{value}||'%'
AND vdr_name LIKE '%'||#{value}||'%'

mapper.update:
  	UPDATE purchaseRequest
	SET status = #{status}
	WHERE pur_req_code =#{purReqCode}
	AND com_id = #{comId}

UPDATE purchaseorderdetail
SET      ITEM_CODE =#{itemCode},
   	  QUANTITY =#{quantity},
   	  SUP_PRICE =#{supPrice},
   	  VAT_PRICE =#{vatPrice},
   	  TOT_PRICE=#{totPrice}
 WHERE pur_ord_dtl_code = #{purOrdDtlCode}
 AND com_id = #{comId}

mapper.delete:
DELETE FROM purchaseorderdetail
WHERE pur_ord_dtl_code = #{purOrdDtlCode}
AND com_id = #{comId}

DELETE FROM purchaseorder
WHERE pur_ord_code = #{purOrdCode}
AND com_id = #{comId}

DELETE FROM purchaseorderdetail
WHERE pur_ord_code = #{purOrdCode}
AND com_id = #{comId}
