mapper.select:
SELECT adj_code, a.item_code, name, ajd_qty, price * ajd_qty price, note FROM adjust a JOIN item i ON(a.item_code = i.item_code) WHERE i.com_id = #{comId}
AND adj_code LIKE '%'||#{value}||'%'
AND name LIKE '%'||#{value}||'%'

SELECT sal_pay_code, base_sal, bonus, extra_allow, tot_price, emp_id, getEmpName(emp_id) emp_name FROM salaryPayment WHERE com_id = #{comId}
AND sal_pay_code LIKE '%'||#{value}||'%'
AND getEmpName(EMP_ID) LIKE '%'||#{value}||'%'

mapper.delete:
   DELETE FROM chit
	WHERE com_id=#{comId}
	AND chit_code= #{chitCode}
